import React from 'react';
// import {SweetAlert, SweetAlertOptionalProps} from 'react-bootstrap-sweetalert';
import swal from 'sweetalert';
//import SweetAlertOptionalProps from 'sweetalert';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';

class Home extends React.Component {

  constructor() {
    super();
    this.state = {
      'title': '',
      'content': '',
      'boxShow': false,
      'token': null,
      'data': []
    }
  }

  componentDidMount() {
    axios({
      method: 'post',
      url: 'https://challenge.ronash.co/react/api/v1/auth/token/',
      // headers: {token: "jwt your_jwt_token"},
      data: { email: 'pourebtehaj.z@gmail.com', password: '987654321' }
    }).then(res => {
      console.log(res)
      this.setState({ 'token': res.data.token })
    })
      .catch(error => console.log(error));
  }

  handleChange = e => {
    this.setState({
      title: e.target.value
    });
  };

  onContentChange = e => {
    this.setState({
      content: e.target.value
    });
  }

  add = (e) => {
    console.log("state:", this.state)
    //sendData();
    this.setState({ 'boxShow': true });
  }

  sendData = () => {
    let { token, title, content } = this.state;
    console.log("token is:", token);
    axios({
      method: 'post',
      url: 'https://challenge.ronash.co/react/api/v1/notes/',
      headers: {
        'Authorization': 'jwt ' + token
      },
      data: { title: title, content: content }
    }).then(res => {
      console.log(res);
      let copyData = [...this.state.data];
      copyData.push(res.data);
      this.setState({ 'data': copyData, 'title': '', 'content': '' })
    })
      .catch(error => console.log(error)
        // swal(error)
      );
  }

  discard = () => {
    this.setState({ 'boxShow': false, 'title': '', 'content': '' });
  }


  render() {
    let { data } = this.state;
    console.log("in render function data is:", this.state.data);
    // if (data !== null) {
    //   let dataList = data.map((data, index) => {
    //     console.log("data is:", data);
    //   });
    // }

    return <React.Fragment>
      <div className={'container-fluid'}>
        <div className={'container'}>
          <div className="card mt-4">
            <div className="card-body">
              <input type="text" className="form-control" id="note" value={this.state.content} placeholder="Take a note ..." onChange={this.onContentChange} />
              <button type="button" className="btn btn-primary" onClick={this.add}>Add</button>
            </div>
          </div>
          {this.state.boxShow && <div className="card mt-4">
            <div className="card-body">
              <input type="text" className="form-control" id="note" value={this.state.title} placeholder="title ..." onChange={this.handleChange} />
              <textarea className="form-control" id="exampleFormControlTextarea1" value={this.state.content} placeholder="Take a note ..." rows="3" onChange={this.onContentChange}></textarea>
              <button type="button" className="btn btn-primary" onClick={this.sendData}>Save</button>
              <button type="button" className="btn btn-primary" onClick={this.discard}>Discard</button>
            </div>
          </div>}
        </ div>
      </div>
      {this.state.data && Object.keys(this.state.data).map((key) => (
        <div className="card mt-4">
          <div className="card-body">
            <div className="todo">
              <h5 className="card-title">{this.state.data[key].title}</h5>
              {/* <h1>{this.state.data.title}</h1> */}
              <h2>{this.state.data[key].content}</h2>
            </div>
          </div>
          <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              Dropdown Button
  </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
          {/* <div className="dropdown">
            <button type="button" className="btn btn-default dropdown-toggle" data-toggle="dropdown">
              <span className="glyphicon glyphicon-option-vertical">
              content:"\e235";
              </span>
            </button>
            <div className="dropdown-menu">
              <a className="dropdown-item" href="#">Link 1</a>
              <a className="dropdown-item" href="#">Link 2</a>
              <a className="dropdown-item" href="#">Link 3</a>
              <div className="dropdown-divider"></div>
              <a clasclassNames="dropdown-item" href="#">Another link</a>
            </div>
          </div> */}
        </div>
      ))}
    </React.Fragment>
  }
}

export default Home;
